bulk.map:
function(doc){
  var bulk = 0;
  var pic_1 = '';
  var pic_1_path = '';
  var pic_2 = '';
  var pic_2_path = '';
  if(doc.archive === false)
  { 
    if(doc.prices.hasOwnProperty("BULK"))
    {
     bulk = doc.prices['BULK']['0'];
    }


if(doc.av0_pic1)
{
  pic_1 = 'http://www.body-piercing.com/img/ABK-1200_' + doc.av0_pic1.replace('/','_');
  pic_1_path = doc.av0_pic1;
}



