doc.av0_style,
doc.av0_code,
bulk,
doc.av0_description,
doc.av0_picture_path_,
doc.av0_gauge,
doc.av0_length,
doc.av0_ball_size,
doc.av0_part_color,
doc.av0_color_1,
doc.av0_color_2,
doc.av0_pic_chart,
doc.av0_metal,
doc.F21_av0_section,
doc.av0_group,
doc.av0_min_in_cust_order,
doc.stock,
'http://www.body-piercing.com/img/ABK-1200_' + doc.av0_picture_path_.replace('/','_'),
doc.av0_weight,
pic_1,
pic_2,
pic_1_path,
pic_2_path,
(doc.av0_Promotion === false) ? "N" : "Y",
cprice
]);
}}
}

